$(function () {
    var timer;
    $('.menu > li').hover(
        function () {
            var $that = $(this);
            timer = setTimeout(function () {
                $that.addClass("active");
                $that.find('ul').finish().slideDown(1);
            }, 500);
        },
        function () {
            clearTimeout(timer);
            $(this).removeClass("active");
            $(this).find('ul').finish().slideUp(1);
        }
    );

});

/* Show Submenu */

setTimeout(function () {
        $('#subnav').show(2000);
        $('.menu-item-has-children > a').css('color', '#f2c900');
    }, 19000
);


$(".menu-item-has-children > a").hover(function () {
        $("#subnav").hide();
    }, function () {
        $("#subnav").delay(19000).show(1000);
    }
);

if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {

    $('.menu-item-has-children > a').on('click', function (e) {
        if (!$(this).hasClass('active')) {
            // отменяем переход по ссылке
            e.preventDefault();
            // что-то делаем...
            console.log("OK");
            // добавляем ссылке класс "active"
            $(this).addClass('active');
        }
    });
    $(document).mouseup(function (e) { // событие клика по веб-документу
        var div = $(".menu-item-has-children > a"); // тут указываем ID элемента
        if (!div.is(e.target) // если клик был не по нашему блоку
            && div.has(e.target).length === 0) { // и не по его дочерним элементам
            $(".menu-item-has-children > a").removeClass('active'); // скрываем его
        }
    });

}

/* Open modal */
$(".modalShow").on("click", function () {
    $(".mask").addClass("show");
});

/* Function for close modal */
function closeModal() {
    $(".mask").removeClass("show");
    $('#submit').attr('disabled', true).removeClass('activeSubmit');
    $('#uAgree').removeClass('activeLink');
    $("form").trigger("reset");
}

/* Modal close */
$(".close, .mask").on("click", function () {
    closeModal();
});

/* Close modal on Esc */
$(document).keyup(function (e) {
    if (e.keyCode == 27) {
        closeModal();
    }
});

$('#customCheck').on('change', function () {
    if ($('#customCheck').prop('checked')) {
        $('#submit').attr('disabled', false).addClass('activeSubmit');
        $('#uAgree').addClass('activeLink');
    } else {
        $('#submit').attr('disabled', true).removeClass('activeSubmit');
        $('#uAgree').removeClass('activeLink');
    }
});


